import React, { useState, FormEvent } from 'react';

import { Link, useHistory } from 'react-router-dom';

import api from '../../services/api';
import PageHeader from '../../components/PageHeader';
import Input from '../../components/Input';

import styles from './styles.css';

function Login() {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const history = useHistory();

  async function handleLogin(e) {
    e.preventDefault();

    try {
      const response = await api.post('session/login', {
        email,
        senha,
      });

      localStorage.setItem('email', email);
      localStorage.setItem('authorization', response.data.token);

      alert('Email enviado para confirmação!');
      history.push('/workstations/list');
    } catch (err) {
      alert('Falha no login, tente novamente.');
    }
  }

  return (
    <div id="page-login" className="container">
      <PageHeader title="Bem-vindo ao gerenciamento de coworking!" />
      <main>
        <form onSubmit={handleLogin}>
          <fieldset>
            <legend>Login</legend>
            <Input type="email" name="Email" label="Email" />
            <Input type="password" name="Senha" label="Senha" />
          </fieldset>

          <button tye="submit">Realizar Cadastro</button>

          <button type="submit" onClick={handleLogin}>
            Realizar LogIn
          </button>
        </form>
        <button
          onClick={() => {
            api.post('session/login', {
              email,
              senha,
            });
          }}
        ></button>
      </main>
    </div>
  );
}

export default Login;
